import React, { Component } from "react";

export default class Counter extends Component {

  constructor() {
    super()
    this.counter=0
  }
   
  render() {
    return (
      <div>
        <p>{this.counter}</p>
        <button onClick={this.setState(this.counter+1)}>Increment Counter</button>
        <button onClick={this.setState(this.counter-1)}>Decrement Counter</button>
      </div>
    );
  }
}
