import "./App.css";
import { useState } from "react";

function App() {

  const [theme, setTheme] = useState("backgroundColor:white");
  const [buttonState, setButtonState] = useState("Light");
  const toggle=()=>{
    if(theme==="white"){
      setTheme("black")
      setButtonState("Dark")
    }
    else{
      setTheme("white")
      setButtonState("Light")
    }
  }
  return <button onClick={toggle} style={theme}> {buttonState}</button>;
}

export default App;
