import "./App.css";
import { useState } from "react";

function App() {
  const [ButtonContent, SetButtonContent] = useState("Show");
  const [content, setContent] = useState("");

  const Toggle = () => {
    if (ButtonContent === "Show") {
      SetButtonContent("Hide");
      setContent("Pankaj Singh Raikwal");
    } else {
      SetButtonContent("Show");
      setContent("");
    }
  };
  return (
    <div>
      <button onClick={Toggle}>{ButtonContent}</button>
      <p>{content}</p>
    </div>
  );
}

export default App;
