import "./App.css";
import { useState ,useEffect} from "react";

function App() {

  const [count, setCount] = useState(0);

  const StartTimer=()=>{
    const value=setInterval(()=>{count=count+1},1000)
  }
  const StopTimer=()=>{
    clearInterval(value)
  }

  return (
    <div>
      <p>{count}</p>
      <button onClick={StartTimer}>Start</button>
      <button onClick={StopTimer}>Stop</button>
    </div>
  );
}

export default App;
