import "./App.css";
import { useState } from "react";

function App() {

  const [counter, setConter] = useState(0);

  const Increment = () => {
    setConter(counter + 1);
  };
  return (
    <div>
      <p>{counter}</p>
      <button onClick={Increment } style={counter==10?{display:"none"}:{display:"block"}} >Increment</button>
    </div>
  );
}

export default App;
