import "./App.css";
import { useState } from "react";

function App() {
  const [theme, setTheme] = useState();
  const [buttonState, setButtonState] = useState("Light");
  const toggle = () => {
    if (theme === "white") {
      setTheme("black");
      setButtonState("Dark");
    } else {
      setTheme("white");
      setButtonState("Light");
    }
  };
  return (
    <div style={{ backgroundColor: "white" }}>
      <button onClick={toggle} >
        {buttonState}
      </button>
    </div>
  );
}

export default App;
