import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [count, setCount] = useState(0);

  function updatecount() {
    setCount(count + 1);
  }
  const StartTimer = () => {
     var value =setInterval(updatecount, 1000);
  };

  const StopTimer=()=>{
    useEffect(() => {
      clearInterval(value);
    });
  }



  return (
    <div>
      <p>{count}</p>
      <button onClick={StartTimer}>Start</button>
      <button onClick={StopTimer}>Stop</button>
    </div>
  );
}

export default App;
